"use strict";const getThemeFromStorage=async()=>{let o=[await chrome.storage.sync.get("baulkoTheme").then(e=>{e=e.baulkoTheme;return console.log("Successfully read baulkoTheme for Chrome: "+e),e}),await browser.storage.sync.get("baulkoTheme").then(e=>{e=e.baulkoTheme;return console.log("Successfully read baulkoTheme for Firefox: "+e),e})];return o.every(e=>e===o[0])||console.error("Themes in storage do not match!"),o[0]},updateTheme=()=>{getThemeFromStorage().then(e=>{e?document.body.parentElement?.classList.add("dark"):document.body.parentElement?.classList.remove("dark")}).then(()=>{document.body.classList.add("transition-colors","duration-300")})};updateTheme();